import Color.*import java.util.*enum class Color {    RED, ORANGE, YELLOW, GREEN}fun main() {    val sc: Scanner = Scanner(System.`in`)    print("Please enter the first number : ")    val inputColor: Color = Color.valueOf(sc.nextLine())    println(getKoreanColorName(inputColor))}fun getKoreanColorName(color: Color): String = when (color) {    RED -> "빨강"    ORANGE -> "노랑"    YELLOW -> "녹색"    GREEN -> "파랑"}