import Color.*import java.time.LocalDateTimeimport java.util.*import kotlin.time.Durationenum class Color {    RED, ORANGE, YELLOW, GREEN}fun main() {    val sc: Scanner = Scanner(System.`in`)    print("Please enter the first number : ")    val inputColor: Color = Color.valueOf(sc.nextLine())    println(printObject(inputColor))    println(getKoreanColorName(inputColor))}fun printObject(obj : Any) :Unit = when (obj) {    is String -> println(obj.lowercase())    is Duration -> println(obj.inWholeNanoseconds)    is LocalDateTime -> println(obj.month)    else -> println(obj.toString())}fun getKoreanColorName(color: Color): String = when (color) {    RED -> "빨강"    ORANGE -> "노랑"    YELLOW -> "녹색"    GREEN -> "파랑"}