import java.io.IOExceptionimport java.util.*fun typeCheck(obj: Any): String = when (obj) {    is Int -> "Integer"    is String -> "String"    else -> throw IOException("Checked Exception")}fun main() {    try {        val sc = Scanner(System.`in`)        println("What is inputType?")        val input: Any = sc.next()        println("Type is " + typeCheck(input))    } catch (e: Exception) {        throw IOException("Checked Exception")    } finally {        println("Done")    }}