fun String.double() = this + thisinfix fun String.add(postfix: String) = this + postfix//fun double(str: String) = str+ strfun main() {    println("to".double())    println(setOf(5, 6, 9, 7, 2, 10, 247).maxOrNull())    println("Hello " add "male")    println(mapOf(1 to "one", 2 to "two", 3 to "three"))    val (name, gender) = Pair("Hello", "man")    mapOf(        "name" to name,        "gender" to gender    ).forEach { (key, value) ->        println("$key -> $value")    }}