import java.util.*fun getMyName(    firstName: String,    lastName: String,): String = "$firstName $lastName"fun getMyNameDefault(    firstName: String = "Done",    lastName: String = "Mars",): String = "$firstName $lastName"const val ONE = 1const val TWO = 2fun sum1(a: Int, b: Int): Int = a + bfun main() {    val sc = Scanner(System.`in`)    println("What is your fist name? ")    val fistName: String = sc.next()    println("What is your last name? ")    val lastName: String = sc.next()    println("Input Name : " + getMyName(fistName, lastName))    println("Input Name : " + getMyName(firstName = fistName, lastName = lastName))    println("Input Name Revers: " + getMyName(lastName = lastName, firstName = fistName))    println("Input Name first and default name : " + getMyNameDefault(firstName = fistName))    println(sum1(ONE, TWO))}