import java.util.*fun main() {    // mutableMapOf 사용법    val students = mutableMapOf<Int, String>()    students[1] = "kotlin"    students[2] = "John"    students[3] = "Peter"    for (student in students) {        println("번호:${student.key}, 이름:${student.value}")    }    for ((num, name) in students) {        println("번호:${num}, 이름:${name}")    }    // mapOf 사용법    val cities = mapOf(        1 to "seoul",        2 to "tokyo",        3 to "shanghai"    )    for (city in cities) {        println("번호:${city.key}, 이름:${city.value}")    }    for ((num, cityName) in cities) {        println("번호:${num}, 이름:${cityName}")    }    // recognize    val sc = Scanner(System.`in`)    println("문자를 입력해주세요.")    val inputData = sc.nextLine()    println("$inputData = ${recognize(inputData)}")}fun recognize(c : String) = when (c) {    in "0".."9" -> "Num"    in "a".."z" -> "Eng"    else -> "Unknown"}