import Error.*import java.util.*sealed class Error {    class FileError(val fileName: String) : Error()    class DatabaseError(val dbmsType: DbmsType) : Error()    class RedisError(val host: String, val port: Int) : Error()}enum class DbmsType {    MYSQL, MARIADB, SQLSERVER}fun getErrorMessage(error: Error) = when (error) {    is FileError -> "Error is occurred at : ${error.fileName}"    is DatabaseError -> "Error on DBMS: ${error.dbmsType}"    is RedisError -> "Error on Redis at : ${error.host}, ${error.port}"}fun main() {    val scan = Scanner(System.`in`)    val inputData = scan.nextLine()    val error: Error = when (inputData) {        "0" -> FileError("Not Find File")        "1" -> DatabaseError(DbmsType.MARIADB)        else -> RedisError(host = "local", port = 3050)    }    println(getErrorMessage(error))}